<div class="row">    <div class="col-xs-12 col-sm-12 col-md-12 col-lg-12">        <div class="box box-primary">            <div class="box-header with-border">                <h3 class="box-title"><em class="fa fa-table">&nbsp;</em>Danh sách bình luận</h3>            </div>            <div class="box-body">                <?php if (!empty($rows)): ?>                    <form class="form-inline" name="main" method="post" action="<?php echo get_admin_url($module_slug . '/main'); ?>" autocomplete="off">                        <div class="table-responsive">                            <table class="table table-striped table-bordered table-hover">                                <thead>                                    <tr>										<th style="width: 160px;">Tác giả</th>                                        <th style="width: 350px;">Bình luận</th>                                        <th>Sản phẩm</th>                                        <th class="text-center">Thời gian</th>										<th class="text-center">Hiện</th>										<!-- <th class="text-center">Đã có đơn hàng xác nhận</th> -->                                        <th style="width: 160px;" class="text-center">Chức năng</th>                                    </tr>                                </thead>                                <tbody>                                    <?php									foreach ($rows as $row) {										?>                                        <tr>											<td>												<a href="<?php echo ('mailto:' . $row['email']); ?>"><img align="left" style="margin-right: 10px;" alt="" src="<?php echo base_url('assets/images/avarta.png'); ?>" height="40" width="40"></a> <strong><?php echo $row['author']; ?></strong>												<a href="mailto:<?php echo $row['email']; ?>"><?php echo $row['email']; ?></a>											</td>											<td>												<?php echo $row['parent'] != 0 ? 'Trả lời <a target="_blank" href="' . $row['link'] . '#comment-' . $row['parent'] . '">' . $row['parent_author'] . '</a><br>' : ''; ?>												<?php echo nl2br($row['comment']); ?>											</td>											<td><a target="_blank" href="<?php echo $row['link']; ?>#data-comments"><?php echo $row['title']; ?></a></td>                                            <td class="text-center"><?php echo display_date($row['created']); ?></td>											<td class="text-center">                                                <input type="checkbox" name="status[]" class="change-status flat-blue" value="<?php echo $row['id']; ?>"<?php if ($row['status'] == 1) echo ' checked="checked"'; ?> />                                            </td>											<!-- <td class="text-center">                                                <input type="checkbox" name="is_verified_purchase[]" class="change-is-verified-purchase flat-blue" value="<?php echo $row['id']; ?>"<?php if ($row['is_verified_purchase'] == 1) echo ' checked="checked"'; ?> />                                            </td> -->                                            <td class="text-center">                                                <em class="fa fa-edit fa-lg">&nbsp;</em> <a href="<?php echo get_admin_url($module_slug . '/content/' . $row['id']); ?>">Sửa</a>                                                &nbsp;-&nbsp;                                                <em class="fa fa-trash-o fa-lg">&nbsp;</em> <a href="<?php echo get_admin_url($module_slug . '/delete/' . $row['id']); ?>" class="delete_bootbox">Xóa</a>                                            </td>                                        </tr>                                        <?php                                    }                                    ?>                                </tbody>                            </table>                        </div>                    </form>                <?php else: ?>                    <div class="callout callout-warning">                        <h4>Thông báo!</h4>                        <p><b>Không</b> có bình luận nào!</p>                    </div>                <?php endif; ?>            </div>			<?php if (isset($pagination) && trim($pagination) != ''): ?>                <div class="box-footer clearfix">                    <?php echo $pagination; ?>                </div>            <?php endif; ?>        </div>    </div></div>