<?phpclass M_comments extends CI_Model {    public $_table_name = 'comments';    function __construst() {        parent::__construst();    }    private function generate_where($args) {                if (isset($args['parent'])) {            $this->db->where($this->_table_name . ".parent", $args['parent']);        }                if (isset($args['product_id'])) {            $this->db->where($this->_table_name . ".product_id", $args['product_id']);        }				if (isset($args['status'])) {            $this->db->where($this->_table_name . ".status", $args['status']);        }    }    private function generate_order_by($args) {        $allow_sort = array("DESC", "ASC");        if (isset($args['order_by']) && is_array($args['order_by']) && !empty($args['order_by'])) {            foreach ($args['order_by'] as $key => $value) {                $sort = in_array($value, $allow_sort) ? $value : "DESC";                $this->db->order_by($key, $sort);            }        }    }        public function gets($args, $perpage = 5, $offset = -1) {        $this->db->select($this->_table_name . '.*, cmt.author as parent_author, shops_rows.title as shops_title');        $this->db->from($this->_table_name);		$this->db->join('shops_rows', 'shops_rows.id = ' . $this->_table_name . '.product_id', 'left');		$this->db->join($this->_table_name . ' as cmt', 'cmt.id = ' . $this->_table_name . '.parent', 'left');        $this->generate_where($args);        $this->generate_order_by($args);        if ($offset >= 0) {            $this->db->limit($perpage, $offset);        }        $query = $this->db->get();        return $query->result_array();    }        public function counts($args) {        $this->db->select();        $this->db->from($this->_table_name);		$this->db->join('shops_rows', 'shops_rows.id = ' . $this->_table_name . '.product_id', 'left');        $this->generate_where($args);        $query = $this->db->get();        return $query->num_rows();    }        public function get($id) {        $this->db->select($this->_table_name . '.*, cmt.author as parent_author, shops_rows.title as shops_title');        $this->db->from($this->_table_name);		$this->db->join('shops_rows', 'shops_rows.id = ' . $this->_table_name . '.product_id', 'left');        $this->db->where($this->_table_name . '.id', $id);        $query = $this->db->get();        return $query->row_array();    }        function get_child($parent) {        $this->db->select($this->_table_name . '.*,');        $this->db->from($this->_table_name);        $this->db->where('parent', $parent);        $this->db->order_by('parent', 'ASC');        $query = $this->db->get();        return $query->result_array();    }        function add($data = array()) {        if (empty($data)) {            return 0;        }        $query = $this->db->insert($this->_table_name, $data);        $insert_id = $this->db->insert_id();        return (isset($query)) ? $insert_id : 0;    }        function update($id, $data) {        if (empty($data)) {            return false;        }        $this->db->where('id', $id);        $query = $this->db->update($this->_table_name, $data);        return (isset($query)) ? true : false;    }    function delete($id) {        $this->db->where_in('id', $id);        $query = $this->db->delete($this->_table_name);        return (isset($query)) ? true : false;    }}