<?phpclass M_shops_price extends CI_Model {    public $_table_name = 'shops_price';    function __construst() {        parent::__construst();    }    function validate_exist_unit($unit) {        $this->db->select();        $this->db->where($this->_table_name . '.unit', $unit);        $query = $this->db->get($this->_table_name);        return $query->result_array();    }    function validate_exist_price($product, $unit) {        $this->db->select();        $this->db->where($this->_table_name . '.product', $product);        $this->db->where($this->_table_name . '.unit', $unit);        $query = $this->db->get($this->_table_name);        return $query->row_array();    }        function get_data($product, $unit) {        $this->db->select();        $this->db->where($this->_table_name . '.product', $product);        $this->db->where($this->_table_name . '.unit', $unit);        $query = $this->db->get($this->_table_name);        return $query->row_array();    }        public function update($product, $unit, $data) {        if (empty($data)) {            return FALSE;        }        $this->db->where($this->_table_name . '.product', $product);        $this->db->where($this->_table_name . '.unit', $unit);        $query = $this->db->update($this->_table_name, $data);        return (isset($query)) ? true : false;    }    function get_price($product, $unit) {        $this->db->select('*');        $this->db->where($this->_table_name . '.product', $product);        $this->db->where($this->_table_name . '.unit', $unit);        $query = $this->db->get($this->_table_name);        return $query->row_array();    }    function get_data_by_product($product) {        $this->db->select();        $this->db->where($this->_table_name . '.product', $product);        $this->db->order_by("order", "ASC");        $query = $this->db->get($this->_table_name);        return $query->result_array();    }    function add($data = array()) {        if (empty($data)) {            return 0;        }        $query = $this->db->insert($this->_table_name, $data);        $insert_id = $this->db->insert_id();        return (isset($query)) ? $insert_id : 0;    }    function admin_delete_by_product($product) {        $this->db->where('product', $product);        $query = $this->db->delete($this->_table_name);        return (isset($query)) ? true : false;    }}/* End of file m_shops_order_details.php *//* Location: ./application/modules/shops/models/m_shops_order_details.php */