l-static face" id="processing-modal" role="dialog" aria-hidden="true">    <div class="modal-dialog">        <div class="modal-content">            <div class="modal-body">                <div class="text-center">                    <img src="<?php echo base_url('assets/images/loading.gif'); ?>" class="icon" />                    <h4>Đang xử lý... </h4>                </div>            </div>        </div>    </div></div><script>    var product_init = [];    <?php if (isset($row['items']) && is_array($row['items']) && !empty($row['items'])): ?>        <?php foreach ($row['items'] as $value) : ?>            var product_item = {};            product_item['product_id'] = "<?php echo $value['product_id']; ?>";            product_item['quantity'] = "<?php echo $value['quantity']; ?>";            product_item['unit_price'] = "<?php echo $value['price']; ?>";            product_item['percent'] = "<?php echo $value['percent_discount']; ?>";            product_item['monetized'] = "<?php echo $value['monetized']; ?>";            product_init.push(product_item);        <?php endforeach; ?>    <?php else: ?>        var product_item = {};        product_item['product_id'] = "0";        product_item['quantity'] = "1";        product_item['unit_price'] = "0";        product_item['percent'] = "0";        product_item['monetized'] = "0";        product_init.push(product_item);    <?php endif; ?>    var products = {};    products[0] = 'Chọn gói sản phẩm';    <?php if (isset($products) && is_array($products) && !empty($products)): ?>        <?php foreach ($products as $value) : ?>            products[<?php echo $value['id'] ?>] = "<?php echo $value['title']; ?>";        <?php endforeach; ?>    <?php endif; ?></script>