<?phpif (!defined('BASEPATH')) {    exit('No direct script access allowed');}include_once APPPATH . '/modules/layout/controllers/Layout.php';class Tags extends Layout {    private $_module_slug = 'tags';    function __construct() {        parent::__construct();        $this->load->model('tags/m_tags', 'M_tags');        $this->_data['breadcrumbs_module_name'] = 'Tags';        $this->_data['breadcrumbs_module_func'] = 'Tags';        $this->_data['module_slug'] = $this->_module_slug;    }    function default_args() {        $order_by = array(            'add_time' => 'DESC'        );        $args = array();        $args['order_by'] = $order_by;        return $args;    }    function counts($args) {        return $this->M_tags->counts($args);    }    function get($id) {        $this->load->model('tags/m_tags', 'M_tags');        return $this->M_tags->get($id);    }    function gets($in_id = array()) {        $this->load->model('tags/m_tags', 'M_tags');        $args = $this->default_args();        if (is_array($in_id) && !empty($in_id)) {            $args = array();            $args['in_id'] = $in_id;        }        return $this->M_tags->gets($args);    }        function get_data_by_alias($alias = '', $module = '') {        $this->load->model('tags/m_tags', 'M_tags');        return $this->M_tags->get_data_by_alias($alias, $module);    }        function get_data_by_name($name = '', $module = '') {        $this->load->model('tags/m_tags', 'M_tags');        return $this->M_tags->get_data_by_name($name, $module);    }    function admin_add($data = array()) {        if (!is_array($data) || empty($data)) {            return FALSE;        }        return $this->M_tags->add($data);    }    function admin_update($id, $data = array()) {        if (!is_array($data) || empty($data)) {            return FALSE;        }        return $this->M_tags->update($id, $data);    }    function admin_delete($in_id = array(0)) {        if (!is_array($in_id) || empty($in_id)) {            return FALSE;        }        return $this->M_tags->delete($in_id);    }}/* End of file tags.php *//* Location: ./application/modules/tags/controllers/tags.php */